{"filter":false,"title":"ex25.rb","tooltip":"/LearningRubyTheHardWay/ex25.rb","ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":23,"column":5},"end":{"row":23,"column":5},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"hash":"7acb9d8ed5a580a61905a23d25a4bc4d96b13edb","undoManager":{"mark":0,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":45,"column":3},"action":"insert","lines":["module Ex25","","  # This function will break up words for us.","  def Ex25.break_words(stuff)","    words = stuff.split(' ')","    return words","  end","","  # Sorts the words.","  def Ex25.sort_words(words)","    return words.sort","  end","","  # Prints the first word after shifting it off.","  def Ex25.print_first_word(words)","    word = words.shift","    puts word","  end","","  # Prints the last word after popping it off.","  def Ex25.print_last_word(words)","    word = words.pop","    puts word","  end","","  # Takes in a full sentence and returns the sorted words.","  def Ex25.sort_sentence(sentence)","    words = Ex25.break_words(sentence)","    return Ex25.sort_words(words)","  end","","  # Prints the first and last words of the sentence.","  def Ex25.print_first_and_last(sentence)","    words = Ex25.break_words(sentence)","    Ex25.print_first_word(words)","    Ex25.print_last_word(words)","  end","","  # Sorts the words then prints the first and last one.","  def Ex25.print_first_and_last_sorted(sentence)","    words = Ex25.sort_sentence(sentence)","    Ex25.print_first_word(words)","    Ex25.print_last_word(words)","  end","","end"],"id":1}]]},"timestamp":1536647426358}